import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const Header = ({ title }) => {
  return (
    <View style={styles.header}>
      <Text style={styles.text}>{title}</Text>
    </View>
  );
};

Header.defaultProps = {
  title: 'Todo List'
};

const styles = StyleSheet.create({
  header: {
    height: 60,
    padding: 15,
    paddingTop: 40,
    backgroundColor: '#1abc9c'
  },
  text: {
    color: '#fff',
    fontSize: 23,
    textAlign: 'center'
  }
});

export default Header;
